mapper.select:
select count(openid_mphelper)
from im_user
where openid_mphelper = #{openidMphelper}

mapper.update:
update im_user
set token=#{token},
    token_time=#{tokenTime}
where openid_mphelper = #{openidMphelper}

update im_user
set nick_name_mphelper=#{nickNameMphelper},
    headimgurl=#{headimgurl}
where openid_mphelper = #{openidMphelper}

mapper.select:
select count(token)
from im_user
where token = #{token}

select TIMESTAMPDIFF(hour,token_time, now()) >= 2 pastDueToken
from im_user
where token = #{token}
